class book extends Execute
{
	
	function __construct()
	{
		parent::__construct();
	}
	
	public static function &singleton()
    {
        static $instance;

        // If the instance is not there, create one
        if (!isset($instance)) {
            $class = __CLASS__;
            $instance = new $class();
        }
        return $instance;
    }
	
	public function _modifyData()
	{
		$this->_SrequestUrl = $this->_Oconf['userSettings']['apiUrl']['book'];
	}
	
    public function _doBook()
	{
		$_Bstatus  = true;
		$_Smessage = '';
		$_Adata	   = array();
		$_Areturn  = array();
		
		//logWrite("\n".$url." \n\nRequest :- \n---------- \n\n".print_r($this,true)."\n","LogXMLLLL-".$this->_IreferenceId,'a+','N');
		
		$this->_modifyData();
		$this->_setData();
		
		$_AbookResult = $this->_executeService(); 
		//$_AbookResult = $this->fun1();//satish
		//$_AbookResult = 	json_decode($_AbookResult,true); 
		
		//print_r($_AbookResult);die;
		$_AbookResponse = array();
		
		if(isset($_AbookResult['Response']['ResponseStatus']) && $_AbookResult['Response']['ResponseStatus'] == 1){
			
			if(isset($_AbookResult['Response']['Response']['PNR']) && !empty($_AbookResult['Response']['Response']['PNR']) && isset($_AbookResult['Response']['Response']['BookingId']) && !empty($_AbookResult['Response']['Response']['BookingId'])){
				$_AbookResponse = $_AbookResult['Response']['Response'];
				
				$_OgetBooking	= controllerGet::getObject('getBooking',$this);
				$_AbookResponse	= $_OgetBooking->parseBookingData($_AbookResponse);
			}
			else{
				$_Bstatus  = false;
				$_Smessage = 'Booking PNR or BookingId Not found';
			}
		}
		else{
			$_Bstatus = false;
			
			if(isset($_AbookResult['Response']['Error']['ErrorMessage']) && !empty($_AbookResult['Response']['Error']['ErrorMessage'])){
				$_Smessage = $_AbookResult['Response']['Error']['ErrorMessage'];
			}
			else{
				$_Smessage = 'Unknown booking error';
			}
		}
		
		if(!$_Bstatus){
			$_Smessage .= " ( API Trace Id :- ".$this->_Ainput['flightTraceId'].")";
		}
		
		$_Areturn = array
					(
						'status' => $_Bstatus,
						'data'   => $_AbookResponse,
						'msg'    => $_Smessage,
					);
					
		return $_Areturn;
	}
